mapper.update:
update board
set bc_id = #{bc_id}, b_title = #{b_title}, b_content = #{b_content}, b_image = #{b_image}
where b_no = #{b_no}

update board
set b_hits = b_hits + 1
where b_no = #{b_no}

mapper.select:

update board
set b_commentcnt = (SELECT count(c_no) FROM comment where b_no = #{b_no})
where b_no = #{b_no};

mapper.update:
UPDATE comment
SET c_no = #{c_no}, u_id = #{u_id}, c_content = #{c_content}
WHERE c_no = #{c_no}
