ec_id = #{ec_id}, pe_name = #{pe_name}, pe_image = #{pe_image}, pe_cause = #{pe_cause},
pe_method = #{pe_method}, pe_biologicalControl = #{pe_biologicalControl}, pe_naturalEnemy = #{pe_naturalEnemy},
